const ButtonOptions = (props) => {
  return props.options.map((option) => (
    <Link
      className={props.css}
      href={option.link}
      style={{
        boxSizing: "border-box",
        borderRadius: "4px",
        border: "1px solid #ECECEC",
      }}
      target="_blank"
    >
      {option.icon}
    </Link>
  ));
};

